create table phone_numbers (id bigint generated by default as identity, phone_number varchar(255), user_id bigint, primary key (id))
create table users (id bigint generated by default as identity, created_date timestamp(6), date_of_birth date, first_name varchar(255), job_title varchar(255), last_name varchar(255), patronymic varchar(255), updated_date timestamp(6), primary key (id))
alter table if exists phone_numbers add constraint phone_numbers_fk_users foreign key (user_id) references users